    public void setMessageBubbleConfiguration(CometChatConfigurations configuration) {
        if (configuration instanceof MessageBubbleConfiguration) {
            MessageBubbleConfiguration config = (MessageBubbleConfiguration) configuration;
            if (config.getLeftMessageRadius() != -1)
                leftMessageRadius = config.getLeftMessageRadius();
            if (config.getLeftMessageCornerRadius() != null)
                leftMessageCornerRadius = config.getLeftMessageCornerRadius();
            if (config.getRightMessageRadius() != -1)
                rightMessageRadius = config.getRightMessageRadius();
            if (config.getRightMessageCornerRadius() != null)
                rightMessageCornerRadius = config.getRightMessageCornerRadius();
            if (config.getAvatarConfiguration() != null)
                avatarConfig = config.getAvatarConfiguration();
            if (config.getMessageReceiptConfiguration() != null)
                messageReceiptConfig = config.getMessageReceiptConfiguration();
            if (config.getSentMessageInputData() != null)
                setSentMessageInputData(config.getSentMessageInputData());
            if (config.getReceiverMessageInputData() != null)
                setReceivedMessageInputData(config.getReceiverMessageInputData());
        }
    }
    private CometChatMessageTemplate getMessageTemplateById(String id) {
        for (CometChatMessageTemplate cometChatMessageTemplate : messageTemplateList) {
            if (cometChatMessageTemplate.getId() != null) {
                if (cometChatMessageTemplate.getId().equalsIgnoreCase(id)) {
                    return cometChatMessageTemplate;
                }
            }
        }
        return null;
    }
